import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './components/Login';
import NurseDashboard from './components/NurseDashboard';
import HeadNurseDashboard from './components/HeadNurseDashboard';
import './App.css';

// Create an Auth Context
const AuthContext = createContext(null);

// Custom hook to use auth context
const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Auth Provider component
const AuthProvider = ({ children }) => {
  const [auth, setAuth] = useState(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    return { token, role };
  });

  useEffect(() => {
    if (auth.token) {
      localStorage.setItem('token', auth.token);
      localStorage.setItem('role', auth.role);
    } else {
      localStorage.removeItem('token');
      localStorage.removeItem('role');
    }
  }, [auth]);

  const logout = () => {
    setAuth({ token: null, role: null });
  };

  return (
    <AuthContext.Provider value={{ auth, setAuth, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Protected Route component
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { auth } = useAuth();

  if (!auth.token) {
    return <Navigate to="/" replace />;
  }

  // Temporarily disable role check to debug navigation
  // if (allowedRoles && !allowedRoles.includes(auth.role)) {
  //   return <Navigate to="/" replace />;


  return children;
};

function App() {
  const { auth, setAuth, logout } = useAuth();

  return (
    <div className="App">
      <header className="App-header">
        <nav>
          {auth.token ? (
            <>
              <span>Welcome, {auth.role}!</span>
              <button onClick={logout}>Logout</button>
            </>
          ) : (
            <>
              {/* <Link to="/">Login</Link */}
              <Login setAuth={setAuth} />

            </>
          )}
        </nav>
      </header>
      <main>
        <Routes>
          <Route path="/" element={<Login setAuth={setAuth} />} />
          {/* Temporarily bypass ProtectedRoute for debugging */}
          <Route
            path="/nurse-dashboard"
            element={<NurseDashboard />}
          />
          <Route
            path="/head-nurse-dashboard"
            element={<HeadNurseDashboard />}
          />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </main>
    </div>
  );
}

// Wrap App with AuthProvider
const AppWrapper = () => (
  <Router>
    <AuthProvider>
      <App />
    </AuthProvider>
  </Router>
);

export default AppWrapper;
