import React, { useState, useEffect } from 'react';
import axios from 'axios';

function NurseDashboard() {
  const [shifts, setShifts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [leaveRequest, setLeaveRequest] = useState({
    shiftId: '',
    reason: '',
  });
  const [leaveMessage, setLeaveMessage] = useState('');

  useEffect(() => {
    const fetchShifts = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:3000/shifts/my-schedule', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setShifts(response.data);
      } catch (err) {
        console.error('Error fetching shifts:', err);
        setError('Failed to fetch shifts.');
      } finally {
        setLoading(false);
      }
    };

    fetchShifts();
  }, []);

  const handleLeaveRequestChange = (e) => {
    setLeaveRequest({ ...leaveRequest, [e.target.name]: e.target.value });
  };

  const handleLeaveRequestSubmit = async (e) => {
    e.preventDefault();
    setLeaveMessage('');
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:3000/leaves/request', leaveRequest, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setLeaveMessage('Leave request submitted successfully!');
      setLeaveRequest({ shiftId: '', reason: '' }); // Clear form
      // Optionally, refetch shifts to update UI
    } catch (err) {
      console.error('Error submitting leave request:', err);
      setLeaveMessage(err.response?.data?.message || 'Failed to submit leave request.');
    }
  };

  if (loading) {
    return <div className="nurse-dashboard-container">Loading shifts...</div>;
  }

  if (error) {
    return <div className="nurse-dashboard-container" style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div className="nurse-dashboard-container">
      <h2>Nurse Dashboard</h2>

      <h3>My Shift Schedule</h3>
      {shifts.length === 0 ? (
        <p>No shifts assigned yet.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Start Time</th>
              <th>End Time</th>
              <th>Ward</th>
            </tr>
          </thead>
          <tbody>
            {shifts.map((shift) => (
              <tr key={shift._id}>
                <td>{new Date(shift.date).toLocaleDateString()}</td>
                <td>{shift.startTime}</td>
                <td>{shift.endTime}</td>
                <td>{shift.ward}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h3>Request Leave</h3>
      <form onSubmit={handleLeaveRequestSubmit}>
        <div>
          <label htmlFor="shiftId">Select Shift:</label>
          <select
            id="shiftId"
            name="shiftId"
            value={leaveRequest.shiftId}
            onChange={handleLeaveRequestChange}
            required
          >
            <option value="">--Select a shift--</option>
            {shifts.map((shift) => (
              <option key={shift._id} value={shift._id}>
                {new Date(shift.date).toLocaleDateString()} ({shift.startTime} - {shift.endTime})
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="reason">Reason for Leave:</label>
          <textarea
            id="reason"
            name="reason"
            value={leaveRequest.reason}
            onChange={handleLeaveRequestChange}
            required
          ></textarea>
        </div>
        <button type="submit">Submit Leave Request</button>
        {leaveMessage && <p>{leaveMessage}</p>}
      </form>
    </div>
  );
}

export default NurseDashboard;
